        let show_cards = document.querySelectorAll('.noShow')

        //给每个菜单栏选项绑定hover事件
        for (let i = 0; i < right_nav_children.length; i++) {
            right_nav_children[i].addEventListener('mouseover', () => {
                //之前hover过的话就进行回复，恢复成图像和隐藏子界面
                if (innerHTML) {
                    right_nav_children[right_nav_index].innerHTML = innerHTML;
                    show_cards[right_nav_index].className = 'noShow'
                }
                //获取当前的html内容，保存，用来当作下一次hover中上一次保留的内容
                innerHTML = right_nav_children[i].innerHTML
                //魔改的，直接用来作html的内容，暴力换行，并显示出定义的文字
                right_nav_children[i].innerHTML = arr[i]
                //修改当前的样式，
                right_nav_children[i].setAttribute('style', 'color:rgb(255,122,1);font-size:13px;font-weight:bolder;')
                // console.log('getBoundingClientRect',right_nav_children[i].getBoundingClientRect().y+'px');
                //展示出对应的子界面
                show_cards[i].className = 'isShow'
                //子界面获取菜单选项栏的高度，用来作为自身的高度
                let top1 = right_nav_children[i].getBoundingClientRect().y + 'px' // right_nav_children[i].clientTop
                show_cards[i].setAttribute('style', `top:${top1}`)
                //判断是否是返回顶部，如果是的话就不需要展示子界面
                if (i == right_nav_children.length - 1) {
                    show_cards[i].className = 'noShow'
                }
                //如果是第一个的话还需要在设置字体靠左边排列
                if (i == 0) {
                    show_cards[i].setAttribute('style', `top:${top1};text-align:left`)
                }
                //设置下一次中上一次被选中的下标
                right_nav_index = i
                show_cards[i].addEventListener('mouseover', () => {
                    right_nav_children[i].innerHTML = arr[i]
                    //修改当前的样式，
                    right_nav_children[i].setAttribute('style', 'color:rgb(255,122,1);font-size:13px;font-weight:bolder')
                    //展示出对应的子界面
                    show_cards[i].className = 'isShow'
                    if (i == right_nav_children.length - 1) {
                        show_cards[i].className = 'noShow'
                    }
                })
                show_cards[i].addEventListener('mouseleave', () => {
                    right_nav_children[right_nav_index].innerHTML = innerHTML;
                    show_cards[i].className = 'noShow'
                })
            })
            //要记得设置离开的回调函数
            right_nav_children[i].addEventListener('mouseleave', () => {
                //恢复菜单栏的图片
                right_nav_children[right_nav_index].innerHTML = innerHTML;
                //隐藏子界面
                show_cards[right_nav_index].className = 'noShow'
            })
        }


        let topBtn = document.querySelector('#backToTop')
        let myTimer = -1;
        topBtn.addEventListener('click', () => {
            let scrollTo = document.documentElement.scrollTop || document.body.scrollTop
            if (myTimer == -1) {
                myTimer = setInterval(() => {
                    scrollTo -= Math.ceil(scrollTo / 50)
                    if (scrollTo <= 0) {
                        scrollTo = 0
                        window.clearInterval(myTimer)
                        myTimer = -1
                    }

                    window.scrollTo(0, scrollTo) //这是值  是指离开网页顶部的距离   
                }, 1);

            }
        })
        //鼠标滚动时阻止继续上滑，增强用户体验
        window.addEventListener("wheel", function (e) {
            let evt = e || window.event;
            window.clearInterval(myTimer)
            myTimer = -1
        });
    }
}
</script>

